import { useEffect } from "react";
import { Device } from "./types";

const BASE_IMAGE_URL = "https://images.svc.ui.com/";

export const IMAGE_SIZE = {
  icon: 24,
  large: 100,
  XL: 292,
};

export function createImageUrl({
  id,
  size,
  imageId,
  quality = 75,
  type = "default",
}: {
  quality: 25 | 50 | 75 | 100;
  id?: string;
  imageId?: string;
  size: string | number;
  type?: "default" | string;
}): string | null {
  if (!id || !imageId) {
    return null; //TODO: Placeholder?
  }
  const imageUrl = new URL(BASE_IMAGE_URL);
  imageUrl.searchParams.append(
    "u",
    `https://static.ui.com/fingerprint/ui/images/${id}/${type}/${imageId}.png`
  );
  imageUrl.searchParams.append("w", String(size));
  imageUrl.searchParams.append("q", String(quality));
  return String(imageUrl);
}

export const fallBackImageSrc =
  "data:image/png;base64,";

const processQueue: string[] = [];

export const useImagePreloader = (
  devices: readonly Readonly<Device>[],
  version: string
) => {
  useEffect(() => {
    // Add new URLs to process queue
    const imageCacheJson = localStorage.getItem("imageCache" + version);
    const imageCache: Record<string, number> = {};
    if (imageCacheJson) {
      try {
        Object.assign(imageCache, JSON.parse(imageCacheJson));
      } catch (e) {
        console.error("Failed to parse image cache", e);
      }
    }

    for (const { images, id } of devices) {
      const largeImageUrl = createImageUrl({
        id,
        imageId: images?.default,
        size: IMAGE_SIZE.large,
        quality: 75,
      });
      const xlImageUrl = createImageUrl({
        id,
        imageId: images?.default,
        size: IMAGE_SIZE.XL,
        quality: 100,
      });

      if (largeImageUrl && largeImageUrl in imageCache === false) {
        processQueue.push(largeImageUrl);
      }
      if (xlImageUrl && xlImageUrl in imageCache === false) {
        processQueue.push(xlImageUrl);
      }
    }

    const newCacheItems: Record<string, number> = {};
    // Process one image at a time
    const processNext = () => {
      if (processQueue.length > 0) {
        const url = processQueue.shift()!;
        const img = new Image();
        img.src = url;
        img.onload = () => {
          newCacheItems[url] = 1;
          processNext();
        };
      } else {
        localStorage.setItem(
          "imageCache" + version,
          JSON.stringify(newCacheItems)
        );
      }
    };

    processNext();
  }, [devices, version]);
};
